import React, { Component } from 'react';
import { getMovies } from '../service/fakeMoviesService';
import { getGenres } from '../service/fakeGenreService';
import Like from './common/like';
import Pagination from './common/pagination';
import { paginate } from '../utils/paginate';
import ListGroup from './common/listGroup';

const MoviesTable = props => {

  const { movies, onDelete, onLike } = props


  const { pageSize, currentPage, movies: allMovies, selectedGenre, genres } = this.state

  const filtered = selectedGenre && selectedGenre._id ? allMovies.filter(ele => ele.genre._id === selectedGenre._id) : allMovies;

  // movies: allMovies 这样写 就是重命名movies
  // const movies = paginate(filtered, currentPage, pageSize)

  return (
    <table className="table">
      <thead>
        <tr>
          <th>Title</th>
          <th>Genre</th>
          <th>Stock</th>
          <th>Rate</th>
          <th>like</th>
          <th>edit</th>
        </tr>
      </thead>
      <tbody>
        {movies.map((ele, i) => (
          <tr key={ele._id}>
            <td>{ele.title}</td>
            <td>{ele.genre.name}</td>
            <td>{ele.numberInStock}</td>
            <td>{ele.dailyRentalRate}</td>
            <td>
              <Like onClick={() => onLike(i)} liked={ele.love} />
            </td>
            <td><button className="btn btn-danger" onClick={() => onDelete(ele)}>delete</button></td>
          </tr>
        ))}
      </tbody>
    </table>
  )

}

export default MoviesTable
