import React from 'react';
import _ from 'lodash';


const ListGroup = props => {
  const { onItemSelect, items, textProperty, valueProperty } = props

  return (
    <nav>
      <ul className="list-group">
        {items.map(ele => (
          <li key={ele[valueProperty]} className={ele ? 'list-group-item' : 'list-group-item'}>
            <a onClick={() => onItemSelect(ele)}>
              {ele[textProperty]}
            </a>
          </li>
        ))}

      </ul>
    </nav>
  )

}
ListGroup.defaultProps = {
  textProperty: 'name',
  valueProperty: '_id'
}

export default ListGroup
