import React, { Component } from 'react';
import { getMovies } from '../service/fakeMoviesService.js';
import Like from './common/like';
import Pagination from './common/pagination';
import { paginate } from '../utils/paginate';

export default class Movies extends Component {

  state = {
    movies: getMovies(),
    pageSize: 4,
    currentPage: 1
  }

  handleDelete(moive) {
    const newMovie = this.state.movies.filter(ele => moive._id !== ele._id)
    this.setState({
      movies: newMovie
    })
  }

  handleLike = (i) => {
    const ele = [...this.state.movies];
    ele[i].love = !ele[i].love;
    this.setState({ movies: ele })
  }

  handleChangePage = page => {
    this.setState({ currentPage: page })
  }
  render() {
    const { length: count } = this.state.movies

    const { pageSize, currentPage, movies: allMovies } = this.state
    // movies: allMovies 这样写 就是重命名movies
    const movies = paginate(allMovies, currentPage, pageSize)
    return (
      count === 0 ?
        <p>还剩下{'nothing'}条</p>
        :
        <React.Fragment>
          <p>还剩下{count}条</p>
          <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Genre</th>
                <th>Stock</th>
                <th>Rate</th>
                <th>like</th>
                <th>edit</th>
              </tr>
            </thead>
            <tbody>
              {movies.map((ele, i) => (
                <tr key={ele._id}>
                  <td>{ele.title}</td>
                  <td>{ele.genre.name}</td>
                  <td>{ele.numberInStock}</td>
                  <td>{ele.dailyRentalRate}</td>
                  <td>
                    <Like onClick={() => this.handleLike(i)} liked={ele.love} />
                  </td>
                  <td><button className="btn btn-danger" onClick={() => this.handleDelete(ele)}>delete</button></td>
                </tr>
              ))}
            </tbody>
          </table>
          <Pagination itemsCount={count} pageSize={'1'} currentPage={currentPage} onPageChange={this.handleChangePage} />
        </React.Fragment>
    )
  }

}
