import React, { Component } from 'react'
import Like from './common/like';

class MoviesTable extends Component {

  render() {
    const { movies, onDelete, onLike, onSort } = props;

    return (
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => onSort('title')}>Title</th>
            <th onClick={() => onSort('genre.name')}>Genre</th>
            <th onClick={() => onSort('numberInStock')}>Stock</th>
            <th onClick={() => onSort('dailyRentalRate')}>Rate</th>
            <th onClick={() => onSort('love')}>like</th>
            <th>edit</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((ele, i) => (
            <tr key={ele._id}>
              <td>{ele.title}</td>
              <td>{ele.genre.name}</td>
              <td>{ele.numberInStock}</td>
              <td>{ele.dailyRentalRate}</td>
              <td>
                <Like onClick={() => onLike(i)} liked={ele.love} />
              </td>
              <td><button className="btn btn-danger" onClick={() => onDelete(ele)}>delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    )

  }
}

export default MoviesTable;
