import React, { Component } from 'react'
import Like from './common/like';

class MoviesTable extends Component {

  raiseSort = path => {
    const { sortColumn: allSort, onSort } = this.props;
    const sortColumn = { ...allSort }
    if (sortColumn.path === path)
      sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';

    else {
      sortColumn.path = path;
      sortColumn.order = 'asc';
    }
    onSort(sortColumn)
  }

  render() {
    const { movies, onDelete, onLike, onSort } = this.props;

    return (
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => this.raiseSort('title')}>Title</th>
            <th onClick={() => this.raiseSor('genre.name')}>Genre</th>
            <th onClick={() => this.raiseSor('numberInStock')}>Stock</th>
            <th onClick={() => this.raiseSor('dailyRentalRate')}>Rate</th>
            <th onClick={() => this.raiseSor('love')}>like</th>
            <th>edit</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((ele, i) => (
            <tr key={ele._id}>
              <td>{ele.title}</td>
              <td>{ele.genre.name}</td>
              <td>{ele.numberInStock}</td>
              <td>{ele.dailyRentalRate}</td>
              <td>
                <Like onClick={() => onLike(i)} liked={ele.love} />
              </td>
              <td><button className="btn btn-danger" onClick={() => onDelete(ele)}>delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    )

  }
}

export default MoviesTable;
