import React, { Component } from 'react'
import Like from './common/like';
import TableHeader from './common/tableHeader';


class MoviesTable extends Component {

  column = [
    { path: 'title', label: 'Title' },
    { path: 'genre.name', label: 'Genre' },
    { path: 'numberInStock', label: 'Stock' },
    { path: 'dailyRentalRate', label: 'Rate' },
    { path: 'love', label: 'like' },
  ]

  render() {
    const { movies, onDelete, onLike } = this.props;

    return (
      <table className="table">
        <TableHeader column={this.column} />
        <tbody>
          {movies.map((ele, i) => (
            <tr key={ele._id}>
              <td>{ele.title}</td>
              <td>{ele.genre.name}</td>
              <td>{ele.numberInStock}</td>
              <td>{ele.dailyRentalRate}</td>
              <td>
                <Like onClick={() => onLike(i)} liked={ele.love} />
              </td>
              <td><button className="btn btn-danger" onClick={() => onDelete(ele)}>delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    )

  }
}

export default MoviesTable;
